//import logo from './logo.svg';
import React, { Component } from "react";
import { Button, Card, Header, Image, Container, Item} from 'semantic-ui-react'
import './App.css';

class Time extends React.Component {
  constructor(props){
      super(props);
      this.timer = 0;
      this.state = {
          time: new Date()
      };
  }
  componentWillMount(){
      // set up timer
      this.timer = setTimeout(() => {
          this.setState({
              time: new Date()
          });
          this.componentWillMount();
      }, Math.floor(Date.now() / 1000) * 1000 + 1000 - Date.now());
  }
  componentWillUnmount(){
      // remove timer
      clearTimeout(this.timer);
  }

  render() {
      // render the current time
      return this.state.time.toLocaleTimeString();
  }
}

class TimerWindow extends Component { 
  constructor(props) {
    super(props);

    this.state = {
      currentTime: <Time />,
      mixTime: ''
    }

    this.onPress = this.onPress.bind(this);
  }

  onPress() { 
    //alert('yooooo');
    let pressDate = Date.now();
    this.setState({ mixTime: pressDate });
    //console.log(this.)
  }

  render() {
    return (
      <div class="ui container">
        <h3 class="ui centered header">Current Time: {this.state.currentTime}</h3>
        <div className='row'>
          <div className='column'>
            <Button onClick={this.onPress}>BAKE</Button>
          </div>
          <div className='column'>
            <Card>
              <Card.Header>
                <Header>Bake Step Times </Header>
              </Card.Header>
              <Card.Content>
              <Card.Description>Mix Initial Ingredients: {this.state.mixTime}</Card.Description>
              <Card.Description>Room Temp Rise: </Card.Description>
              <Card.Description>Cold Rise: </Card.Description>
              <Card.Description>Bring To Room Temp: </Card.Description>
              <Card.Description>More Ingredients: </Card.Description>
              <Card.Description>Rise Room Temp: </Card.Description>
              <Card.Description>Proof: </Card.Description>
              <Card.Description>Bake: </Card.Description>
              <Card.Description>Cool time: </Card.Description>
              </Card.Content>
            </Card>
          </div> 
        </div>
      </div>
    ); 
  }
}


function App() {
  return (
    <div>
      <TimerWindow />
    </div>
  );
}

export default App;
