//import logo from './logo.svg';
import React, { Component } from "react";
import { Button, Card, Header, Image, Container} from 'semantic-ui-react'
import './App.css';

class TimerWindow extends Component { 
  constructor(props) {
    super(props);

    var today = new Date(),
    time = today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds();

    this.state = {
      currentTime: time
    }

    this.onPress = this.onPress.bind(this);
  }

  onPress() { 
    this.props.onVote(this.props.id);
  }

  render() {
    return (
      <div className='row'>
        <div className='column'>
          <Button onClick={this.onPress}>BAKE</Button>
        </div>
        <div className='column'>
          <Card>
            <Card.Header>
              <Header>Bake Step Times {this.state.currentTime}</Header>
            </Card.Header>
            <Card.Content>
            <Card.Description>Mix Initial Ingredients: </Card.Description>
            <Card.Description>Room Temp Rise: </Card.Description>
            <Card.Description>Cold Rise: </Card.Description>
            <Card.Description>Bring To Room Temp: </Card.Description>
            <Card.Description>More Ingredients: </Card.Description>
            <Card.Description>Rise Room Temp: </Card.Description>
            <Card.Description>Proof: </Card.Description>
            <Card.Description>Bake: </Card.Description>
            <Card.Description>Cool time: </Card.Description>
            </Card.Content>
          </Card>
        </div> 
      </div>
    ); 
  }
} 

function App() {
  return (
    <div>
      <TimerWindow />
    </div>
  );
}

export default App;
