import React, { Component } from "react";
import { Button, Card, Header, Transition, Image} from 'semantic-ui-react'
import './App.css';
import baguette from './images/baguettecard.png';

class StepTimer extends React.Component {
  constructor(props){
    super(props);

    this.timer = 0;
    this.state = {
        time: new Date()
    };

  }

  componentWillMount(){
    // set up timer
    this.timer = setTimeout(() => {
        this.setState({
            time: new Date()
        });
        this.componentWillMount();
    }, Math.floor(Date.now() / 1000) * 1000 + 1000 - Date.now());
  }

  componentWillUnmount(){
    // remove timer
    clearTimeout(this.timer);
  }

  msToTime(duration) {
    var milliseconds = parseInt((duration % 1000) / 100),
      seconds = Math.floor((duration / 1000) % 60),
      minutes = Math.floor((duration / (1000 * 60)) % 60),
      hours = Math.floor((duration / (1000 * 60 * 60)) % 24);

    hours = (hours < 10) ? "0" + hours : hours;
    minutes = (minutes < 10) ? "0" + minutes : minutes;
    seconds = (seconds < 10) ? "0" + seconds : seconds;

    return "Timer: " + hours + ":" + minutes + ":" + seconds;
  }


  render() {
    let timeLeft = this.props.startTime + this.props.nextStepTime;
    return this.msToTime((timeLeft - this.state.time) * 1000);
  }
}

